import { bootstrapApplication } from '@angular/platform-browser';
import { AppComponent } from './app/app.component';
import { importProvidersFrom } from '@angular/core';
import { AppRoutingModule } from './app/app-routing.module';
import { FormsModule } from '@angular/forms';
import { HttpClientModule } from '@angular/common/http';
import { provideAnimations } from '@angular/platform-browser/animations';
import { withViewTransitions } from '@angular/router'; // ✅ nuevo import
bootstrapApplication(AppComponent, {
  providers: [importProvidersFrom(AppRoutingModule, FormsModule, HttpClientModule), provideAnimations(), withViewTransitions() // ✅ activa animaciones entre páginas
  ]
}).catch(err => console.error(err));
