export function startLeafRain() {
  const container = document.querySelector('.leaves-layer');
  if (!container) return;
  const TARGET_LEAVES = 24;
  const createLeaf = () => {
    const leaf = document.createElement('div');
    leaf.className = 'leaf';
    // posición horizontal random (0% a 100%)
    const left = Math.random() * 100;
    // duración de caída entre 16s y 26s
    const duration = 16 + Math.random() * 10;
    // delay entre 0 y 4s -> siempre parte arriba, pero no todas juntas
    const delay = Math.random() * 4;
    // ligera rotación inicial [-20°, 20°]
    const rotate = (Math.random() - 0.5) * 40;
    // escala entre 0.8 y 1.3 → hojas más grandes o pequeñas
    const scale = 0.8 + Math.random() * 0.5;
    // modelo de hoja (1, 2 o 3)
    const variant = 1 + Math.floor(Math.random() * 3);
    leaf.setAttribute('data-variant', String(variant));
    // aplicamos estilos inline
    leaf.style.left = `${left}%`;
    leaf.style.animationDuration = `${duration}s`;
    leaf.style.animationDelay = `${delay}s`;
    leaf.style.transform = `rotate(${rotate}deg) scale(${scale})`;
    container.appendChild(leaf);
    // la eliminamos cuando termine su animación (duración + delay)
    const totalLife = (duration + delay) * 1000;
    setTimeout(() => {
      leaf.remove();
    }, totalLife + 500); // pequeño margen extra
  };
  // primer "batch" inicial de hojas
  for (let i = 0; i < TARGET_LEAVES; i++) {
    createLeaf();
  }
  // cada cierto tiempo agregamos nuevas hojas
  setInterval(() => {
    const currentLeaves = container.querySelectorAll('.leaf').length;
    if (currentLeaves < TARGET_LEAVES * 1.4) {
      const toCreate = 1 + Math.floor(Math.random() * 3);
      for (let i = 0; i < toCreate; i++) {
        createLeaf();
      }
    }
  }, 1200);
}
